import React, { useState, useEffect } from 'react';
import OrderBookTable from './OrderBookTable';
import OrderDetail from './OrderDetail';
import ExchangeManager from './ExchangeManager';
import TokenManager from './TokenManager';
import Settings from './Settings';
import WebSocketService from '../services/websocket';
import '../App.css';

// URL –¥–ª—è WebSocket-–∑'—î–¥–Ω–∞–Ω–Ω—è –∑ –±–µ–∫–µ–Ω–¥–æ–º
const WS_URL = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';

function App() {
  // –°—Ç–∞–Ω –¥–æ–¥–∞—Ç–∫—É
  const [wsService, setWsService] = useState(null);
  const [connected, setConnected] = useState(false);
  const [tokens, setTokens] = useState([]);
  const [exchanges, setExchanges] = useState([]);
  const [orderbooks, setOrderbooks] = useState({});
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [thresholds, setThresholds] = useState({
    percentThreshold: 5.0,  // 5%
    deltaThreshold: 0.5     // 0.5 USDT
  });
  const [isDarkTheme, setIsDarkTheme] = useState(true);

  // –Ü–Ω—ñ—Ü—ñ–∞–ª—ñ–∑–∞—Ü—ñ—è WebSocket-—Å–µ—Ä–≤—ñ—Å—É
  useEffect(() => {
    const service = new WebSocketService(WS_URL);

    // –†–µ—î—Å—Ç—Ä–∞—Ü—ñ—è –æ–±—Ä–æ–±–Ω–∏–∫—ñ–≤ –ø–æ–¥—ñ–π
    service
      .on('onOpen', () => setConnected(true))
      .on('onClose', () => setConnected(false))
      .on('onInitialData', (data) => {
        setTokens(data.tokens || []);
        setExchanges(data.exchanges.map(e => e.name) || []);
        setOrderbooks(data.orderbooks || {});
      })
      .on('onOrderbookUpdate', (data) => {
        setOrderbooks(prev => {
          const newOrderbooks = { ...prev };

          if (!newOrderbooks[data.token]) {
            newOrderbooks[data.token] = {};
          }

          if (!newOrderbooks[data.token][data.exchange]) {
            newOrderbooks[data.token][data.exchange] = {};
          }

          newOrderbooks[data.token][data.exchange] = {
            best_sell: data.best_sell,
            best_buy: data.best_buy,
            sells: data.sell,
            buys: data.buy
          };

          return newOrderbooks;
        });

        // –Ø–∫—â–æ —Ü–µ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è –¥–ª—è –æ–±—Ä–∞–Ω–æ–≥–æ –æ—Ä–¥–µ—Ä—É, –æ–Ω–æ–≤–ª—é—î–º–æ –π–æ–≥–æ –¥–µ—Ç–∞–ª—ñ
        if (selectedOrder && 
            selectedOrder.token === data.token && 
            selectedOrder.exchange === data.exchange) {
          setSelectedOrder(prev => ({
            ...prev,
            orders: selectedOrder.type === 'sell' ? data.sell : data.buy
          }));
        }
      })
      .on('onTokenAdded', (token) => {
        setTokens(prev => [...prev, token]);
      })
      .on('onTokenRemoved', (token) => {
        setTokens(prev => prev.filter(t => t !== token));
      })
      .on('onExchangeAdded', (exchange) => {
        setExchanges(prev => [...prev, exchange.name]);
      })
      .on('onExchangeRemoved', (exchange) => {
        setExchanges(prev => prev.filter(e => e !== exchange));
      })
      .on('onOrderbooksCleared', () => {
        setOrderbooks({});
      });

    // –ü—ñ–¥–∫–ª—é—á–µ–Ω–Ω—è –¥–æ WebSocket
    service.connect();
    setWsService(service);

    // –í—ñ–¥–∫–ª—é—á–µ–Ω–Ω—è –ø—Ä–∏ —Ä–æ–∑–º–æ–Ω—Ç—É–≤–∞–Ω–Ω—ñ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    return () => {
      service.disconnect();
    };
  }, []);

  // –û–±—Ä–æ–±–Ω–∏–∫ –∫–ª—ñ–∫—É –Ω–∞ –∫–ª—ñ—Ç–∏–Ω—É —Ç–∞–±–ª–∏—Ü—ñ
  const handleCellClick = (token, exchange, type) => {
    const data = orderbooks[token]?.[exchange];

    if (!data) return;

    setSelectedOrder({
      token,
      exchange,
      type,
      orders: type === 'sell' ? data.sells : data.buys
    });
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –¥–æ–¥–∞–≤–∞–Ω–Ω—è —Ç–æ–∫–µ–Ω–∞
  const handleAddToken = (token) => {
    if (wsService) {
      wsService.addToken(token);
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –≤–∏–¥–∞–ª–µ–Ω–Ω—è —Ç–æ–∫–µ–Ω–∞
  const handleRemoveToken = (token) => {
    if (wsService) {
      wsService.removeToken(token);
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –¥–æ–¥–∞–≤–∞–Ω–Ω—è –±—ñ—Ä–∂—ñ
  const handleAddExchange = (name, url, type) => {
    if (wsService) {
      wsService.addExchange(name, url, type);
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –≤–∏–¥–∞–ª–µ–Ω–Ω—è –±—ñ—Ä–∂—ñ
  const handleRemoveExchange = (exchange) => {
    if (wsService) {
      wsService.removeExchange(exchange);
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –æ–Ω–æ–≤–ª–µ–Ω–Ω—è —Ü—ñ–Ω
  const handleUpdatePrices = (exchange = null) => {
    if (wsService) {
      wsService.updatePrices(exchange);
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –æ—á–∏—â–µ–Ω–Ω—è –æ—Ä–¥–µ—Ä–±—É–∫—ñ–≤
  const handleClearOrderbooks = () => {
    if (wsService) {
      wsService.clear();
    }
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –∑–º—ñ–Ω–∏ –ø–æ—Ä–æ–≥—ñ–≤ –ø—ñ–¥—Å–≤—ñ—á—É–≤–∞–Ω–Ω—è
  const handleThresholdsChange = (newThresholds) => {
    setThresholds(newThresholds);
  };

  // –û–±—Ä–æ–±–Ω–∏–∫ –∑–∞–∫—Ä–∏—Ç—Ç—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –≤—ñ–∫–Ω–∞ –∑ –¥–µ—Ç–∞–ª—è–º–∏ –æ—Ä–¥–µ—Ä—É
  const handleCloseOrderDetail = () => {
    setSelectedOrder(null);
  };

  // –î–æ–¥–∞—î–º–æ –æ–±—Ä–æ–±–Ω–∏–∫ –∑–º—ñ–Ω–∏ —Ç–µ–º–∏
  const handleThemeToggle = () => {
    setIsDarkTheme(prev => !prev);
    document.body.className = isDarkTheme ? 'light-theme' : 'dark-theme';
  };

  useEffect(() => {
    // –í—Å—Ç–∞–Ω–æ–≤–ª—é—î–º–æ –ø–æ—á–∞—Ç–∫–æ–≤—É —Ç–µ–º—É
    document.body.className = isDarkTheme ? 'dark-theme' : 'light-theme';
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
          <h1>Crypto Orderbook Dashboard</h1>
          <div className="status-group">
            <div className="connection-status">
              <span className={`status-indicator ${connected ? 'connected' : 'disconnected'}`}></span>
              {connected ? 'Connected' : 'Disconnected'}
            </div>
            <div className="status-legend">
              <div className="legend-item">
                <span className="status-indicator has-data"></span>
                <span>–Ñ –¥–∞–Ω—ñ</span>
              </div>
              <div className="legend-item">
                <span className="status-indicator no-data"></span>
                <span>–ù–µ–º–∞—î –¥–∞–Ω–∏—Ö</span>
              </div>
              <div className="legend-item">
                <span className="status-indicator disconnected"></span>
                <span>–í—ñ–¥–∫–ª—é—á–µ–Ω–æ</span>
              </div>
            </div>
          </div>
          <button 
            onClick={handleThemeToggle}
            style={{
              padding: '8px 16px',
              fontSize: '18px',
              backgroundColor: isDarkTheme ? '#2d2d2d' : '#f0f0f0',
              border: '1px solid',
              borderColor: isDarkTheme ? '#555' : '#ddd',
              borderRadius: '8px',
              cursor: 'pointer',
              color: isDarkTheme ? '#fff' : '#000',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              minWidth: '40px'
            }}
          >
            {isDarkTheme ? 'üåô' : '‚òÄÔ∏è'}
          </button>
        </div>
      </header>

      <div className="app-content">
        <div className="sidebar">
          <TokenManager 
            tokens={tokens} 
            onAddToken={handleAddToken} 
            onRemoveToken={handleRemoveToken} 
          />

          <ExchangeManager 
            exchanges={exchanges} 
            onAddExchange={handleAddExchange} 
            onRemoveExchange={handleRemoveExchange} 
            onUpdatePrices={handleUpdatePrices}
          />

          <Settings 
            thresholds={thresholds} 
            onThresholdsChange={handleThresholdsChange} 
          />

          <div className="actions">
            <button onClick={() => handleUpdatePrices()} className="update-button">
              Update All Prices
            </button>
            <button onClick={handleClearOrderbooks} className="clear-button">
              Clear Orderbooks
            </button>
          </div>
        </div>

        <div className="main-content">
          <OrderBookTable 
            orderbooks={orderbooks}
            exchanges={exchanges}
            tokens={tokens}
            thresholds={thresholds}
            onCellClick={handleCellClick}
          />
        </div>
      </div>

      {selectedOrder && (
        <OrderDetail 
          order={selectedOrder} 
          onClose={handleCloseOrderDetail} 
        />
      )}
    </div>
  );
}

export default App;
